mapper.select:
select t3.num,t3.title,t3.nickname,t3.w_date,t3.hit,t3.recommend from(
			select trunc((rn-1)/10+1) as page,t2.* from(
				select rownum rn,t1.* from(
		  			 select num,LPAD('└',(LEVEL-1)*2,'　')||title as title,nickname,w_date,hit,recommend from reviewBoard start with ref=0 connect by prior num=ref order siblings by num desc
		  		) t1
			 )t2
		)t3 where page=#{page}
